import React from 'react'
import CardMesure from './CardMesure'
import { connect } from 'react-redux'

class Mesures extends React.Component {
	state = {
		u2eff: 0,
		i1eff: 0,
		i2eff: 0,
		p1: 0,
		p2: 0,
		rendement: 0
	}

	//
	render() {
		const { u2eff, i1eff, i2eff } = this.props.mesures
		return (
			<div className="fluid">
				<div className="row">
					<div className="col s12 m4">
						<CardMesure
							color={'#ffa000'}
							max={250}
							value={u2eff}
							label={'U2eff(V)'}
						/>
					</div>

					<div className="col  s12 m4">
						<CardMesure
							color={'#ffa000'}
							max={10}
							value={i1eff}
							label={'I1eff(A)'}
						/>
					</div>

					<div className="col s12 m4">
						<CardMesure
							color={'#ffa000'}
							max={10}
							value={i2eff}
							label={'I2eff(A)'}
						/>
					</div>

					<div className="col s12 m4">
						<CardMesure
							color={'#ff6f00'}
							max={2000}
							value={this.state.p1}
							label={'P1(W)'}
						/>
					</div>

					<div className="col s12 m4">
						<CardMesure
							color={'#ff6f00'}
							max={2000}
							value={this.state.p2}
							label={'P2(W)'}
						/>
					</div>

					<div className="col s12 m4">
						<CardMesure
							color={'#ff6f00'}
							max={100}
							value={this.state.rendement}
							label={'Rendement(%)'}
						/>
					</div>
				</div>
			</div>
		)
	}
}

const mapStateToProps = state => {
	return {
		mesures: state.mesures
	}
}

export default connect(mapStateToProps)(Mesures)
