import { SET_MODELE_KAPP, SET_NEW_REGLAGE } from './types'

const initState = {
	modeleKapp: {},
	reglages: {},
	mesures: {}
}

const process = (kapp, reglage) => {
	console.log(kapp)
	console.log(reglage)
}

const rootReducer = (state = initState, action) => {
	switch (action.type) {
		//
		case SET_MODELE_KAPP: {
			process(action.payload, state.reglages)
			return {
				...state,
				modeleKapp: { ...action.payload }
			}
		}
		//
		case SET_NEW_REGLAGE: {
			process(state.kapp, action.payload)
			//
			return {
				...state,
				reglages: { ...action.payload }
			}
		}
		//
		default:
			return state
	}
}

export default rootReducer
