import React from 'react'
import CardMesure from './CardMesure'
import { connect } from 'react-redux'
import PropTypes from 'prop-types'
import { RadialGauge } from 'react-canvas-gauges'

class Mesures extends React.Component {
	//
	static propTypes = {
		mesures: PropTypes.shape({
			u2eff: PropTypes.string.isRequired,
			i1eff: PropTypes.string.isRequired,
			i2eff: PropTypes.string.isRequired,
			p1: PropTypes.string.isRequired,
			p2: PropTypes.string.isRequired,
			rendement: PropTypes.string.isRequired
		}).isRequired
	}
	//
	render() {
		const { u2eff, i1eff, i2eff, p1, p2, rendement } = this.props.mesures
		return (
			<div className="fluid">
				<div className="row">
					<div className="col s12 m4">
						<RadialGauge
							units="V"
							width={350}
							height={300}
							title="U2eff"
							value={u2eff}
							minValue={0}
							maxValue={300}
							majorTicks={['0', '50', '100', '150', '200', '250', '300']}
							minorTicks={10}
							highlights={[{ from: 0, to: 300, color: 'rgba(0,255,0,.15)' }]}
						/>
						{/* <CardMesure
							color={'#ffb74d'}
							max={250}
							value={u2eff}
							label={'U2eff(V)'}
						/> */}
					</div>

					<div className="col  s12 m4">
						<RadialGauge
							units="A"
							width={350}
							height={300}
							title="I1eff"
							value={i1eff}
							minValue={0}
							maxValue={10}
							majorTicks={['0', '2', '4', '6', '8', '10']}
							highlights={[{ from: 0, to: 10, color: 'rgba(0,255,0,.15)' }]}
						/>
						{/* <CardMesure
							color={'#ffb74d'}
							max={10}
							value={i1eff}
							label={'I1eff(A)'}
						/> */}
					</div>

					<div className="col s12 m4">
						<RadialGauge
							units="A"
							width={350}
							height={300}
							title="I2eff"
							value={i2eff}
							minValue={0}
							maxValue={10}
							majorTicks={['0', '2', '4', '6', '8', '10']}
							highlights={[{ from: 0, to: 10, color: 'rgba(0,255,0,.15)' }]}
						/>
						{/* <CardMesure
							color={'#ffb74d'}
							max={10}
							value={i2eff}
							label={'I2eff(A)'}
						/> */}
					</div>

					<div className="col s12 m4">
						<CardMesure
							color={'#ffb74d'}
							max={2000}
							value={p1}
							label={'P1(W)'}
						/>
					</div>

					<div className="col s12 m4">
						<CardMesure
							color={'#ffb74d'}
							max={2000}
							value={p2}
							label={'P2(W)'}
						/>
					</div>

					<div className="col s12 m4">
						<CardMesure
							color={'#ffb74d'}
							max={100}
							value={rendement}
							label={'Rendement(%)'}
						/>
					</div>
				</div>
			</div>
		)
	}
}

const mapStateToProps = state => {
	return {
		mesures: state.mesures
	}
}

export default connect(mapStateToProps)(Mesures)
